mapper.select:
Select a.ID, -- 아이디 a.TYPE, -- 타입 a.TITLE, -- 제목 a.AUTHOR_NAME, -- 작성자 a.CREATED_AT, -- 작성일자 a.VIEWS, -- 조회수 a.IS_ACTIVE -- 활성화여부 FROM POSTS a Where 1 = 1 And a.TYPE = 'EVENT'
And Upper(a.TITLE) Like '%' || Upper(#{title}) || '%'
AND Date(a.created_at) >= #{begDate}
AND Date(a.created_at) <= #{endDate}
Order By Date(a.CREATED_AT) Desc
