mapper.select:
Select * From ( Select a.id, -- 주문_ID a.all_ticket_used, -- 모든티켓사용여부 a.ordered_at, -- 주문일시 a.order_number, -- 주문번호 STRING_AGG(DISTINCT d.name, ', ') AS partner_name, a.customer_name, -- 주문자 a.customer_phone, -- 주문자 연락처 STRING_AGG(b.product_name, ', ') AS product_name, SUM(b.quantity) AS product_cnt, a.total_price, -- 상품총금액 a.discount_price, -- 할인금액 a.status, -- 주문상태 a.payment_status, -- 결제상태 a.final_price, -- 결제금액 a.payment_method -- 결제수단 FROM orders a JOIN order_items b ON b.order_id = a.id JOIN products c ON c.id = b.product_id JOIN partners d ON d.id = c.partner_id GROUP BY a.id ) a Where 1 = 1
AND ( Upper(a.ORDER_NUMBER) LIKE '%' || Upper(#{srchWord}) || '%' OR Upper(a.CUSTOMER_NAME) LIKE '%' || Upper(#{srchWord}) || '%' OR Upper(a.PRODUCT_NAME) LIKE '%' || Upper(#{srchWord}) || '%' )
AND Date(a.ordered_at) >= #{begDate}
AND Date(a.ordered_at) <= #{endDate}
AND a.STATUS = #{status}::order_status
Order By a.ordered_at Desc

mapper.update:
UPDATE orders
SET
    payment_status = 'PAID',
    paid_at = #{paidAt}
WHERE id = #{id}
AND payment_status = 'READY'

UPDATE orders
SET status = 'COMPLETED'
WHERE id = #{id}
