choose.otherwise:
DELETE FROM PRODUCT_SECTION
WHERE PRODUCT_ID = #{id}
AND SECTION_ID = #{sectionId};

mapper.delete:
DELETE FROM PRODUCTS
WHERE 1 = 1
And ID = #{id}

DELETE FROM PRODUCT_OPTION_VALUES
WHERE ID IN

    #{id}

DELETE FROM PRODUCT_OPTION_VALUES
WHERE 1 = 1
And OPTION_ID = #{id}

DELETE FROM PRODUCT_OPTIONS
WHERE 1 = 1
And ID = #{id}

mapper.select:
SELECT COALESCE(MAX(group_no), 0) + 1
FROM stay_date_prices
WHERE option_value_id = #{id}

mapper.delete:
DELETE FROM stay_date_prices
WHERE 1 = 1
And option_value_id = #{id}
And GROUP_NO = #{groupNo}

DELETE FROM SECTION
WHERE 1 = 1
And ID = #{id}

mapper.update:

UPDATE section
SET display_order = display_order + 1
WHERE display_order >= #{newOrder}


UPDATE section
SET display_order = display_order + 1
WHERE display_order >= #{newOrder}
AND display_order <  #{oldOrder}


UPDATE section
SET display_order = display_order - 1
WHERE display_order <= #{newOrder}
AND display_order >  #{oldOrder}
